import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AuthForm = () => {
  const [isSignUp, setIsSignUp] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [role, setRole] = useState("user"); // Default role
  const navigate = useNavigate();

  const toggleMode = () => {
    setIsSignUp(!isSignUp);
    setEmail("");
    setPassword("");
    setName("");
    setRole("user");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isSignUp) {
        await axios.post("http://localhost:5000/api/signup", {
          name,
          email,
          password,
          role,
        });
        alert("‚úÖ Account Created Successfully!");
      } else {
        const res = await axios.post("http://localhost:5000/api/login", { email, password });
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("role", res.data.role);

        alert("üöÄ Logged in successfully!");

        if (res.data.role === "admin") {
          navigate("/admin-dashboard");
        } else {
          navigate("/tasks");
        }
      }
    } catch (error) {
      alert("‚ùå Error: " + error.response.data.message);
    }
  };

  return (
    <div className="auth-container">
      <h2>{isSignUp ? "Sign Up" : "Sign In"}</h2>
      <form onSubmit={handleSubmit}>
        {isSignUp && (
          <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        )}

        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />

        {isSignUp && (
          <div>
            <label>
              <input type="radio" value="user" checked={role === "user"} onChange={() => setRole("user")} /> User
            </label>
            <label>
              <input type="radio" value="admin" checked={role === "admin"} onChange={() => setRole("admin")} /> Admin
            </label>
          </div>
        )}

        <button type="submit">{isSignUp ? "Sign Up" : "Sign In"}</button>
      </form>

      <p onClick={toggleMode}>
        {isSignUp ? "Already have an account? Sign In" : "New user? Sign Up"}
      </p>
    </div>
  );
};

export default AuthForm;
