// Importing necessary modules and styles
import React, { useState, useEffect } from "react";
import "./AuthForm.css"; // Import external CSS for styling
import { FaUser, FaEnvelope, FaLock, FaEye, FaEyeSlash } from "react-icons/fa"; // Import icons for better UI
import { useNavigate } from "react-router-dom";

// Authentication Form Component
const AuthForm = () => {
  const [isSignUp, setIsSignUp] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [role, setRole] = useState("user"); // Default role is "user"
  const [showPassword, setShowPassword] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState("");

  const navigate = useNavigate();

  // Function to toggle between Sign In & Sign Up modes
  const toggleMode = () => {
    setIsSignUp(!isSignUp);
    setEmail("");
    setPassword("");
    setName("");
    setRole("user"); // Reset role to "user" when switching
    setShowPassword(false);
  };

  // Handles form submission for Sign In/Sign Up
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!email.trim()) {
      alert("Email is required!");
      return;
    }

    setPopupMessage(isSignUp ? "âœ… Account Created Successfully!" : "ðŸš€ Welcome back!");

    setShowPopup(true);

    console.log({
      name,
      email,
      password,
      role,
    });

    // Redirect based on role
    setTimeout(() => {
      setShowPopup(false);
      if (isSignUp) {
        alert(`Account created as ${role}!`);
      }

      if (role === "admin") {
        navigate("/admin-dashboard"); // Redirect to admin panel
      } else {
        navigate("/tasks"); // Redirect to user task management page
      }
    }, 3000);
  };

  return (
    <div className="auth-container">
      <div className="auth-left">
        <h2 className="auth-title">
          Zidio Task <br />
          Management
        </h2>
        <div className="auth-tagline">Manage all your tasks in one place</div>
      </div>

      <div className="auth-box">
        <h2>{isSignUp ? "Sign Up" : "Sign In"}</h2>

        <form onSubmit={handleSubmit}>
          {isSignUp && (
            <div className="input-group">
              <FaUser className="icon" />
              <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
          )}

          <div className="input-group">
            <FaEnvelope className="icon" />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <FaLock className="icon" />
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              minLength="6"
            />
            <span
              className="toggle-password"
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>

          {/* Role Selection for Sign Up */}
          {isSignUp && (
            <div className="role-selection">
              <label>
                <input
                  type="radio"
                  value="user"
                  checked={role === "user"}
                  onChange={() => setRole("user")}
                />
                User
              </label>
              <label>
                <input
                  type="radio"
                  value="admin"
                  checked={role === "admin"}
                  onChange={() => setRole("admin")}
                />
                Admin
              </label>
            </div>
          )}

          {!isSignUp && (
            <p className="forgot-password">
              Lost password? <span>Click Here!</span>
            </p>
          )}

          <button type="submit" className="submit-btn">
            {isSignUp ? "Sign Up" : "Sign In"}
          </button>
        </form>

        <p className="switch-mode" onClick={toggleMode}>
          {isSignUp ? "Already have an account? Sign In" : "New user? Sign Up"}
        </p>
      </div>

      {showPopup && (
        <div className="popup">
          <p>{popupMessage}</p>
        </div>
      )}
    </div>
  );
};

export default AuthForm;
