// Importing necessary modules and styles
import React, { useState, useEffect } from "react";
import "./AuthForm.css"; // Import external CSS for styling
import { FaUser, FaEnvelope, FaLock, FaEye, FaEyeSlash } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

// Authentication Form Component
const AuthForm = () => {
  // State variables for form inputs and UI behavior
  const [isSignUp, setIsSignUp] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState("");

  const navigate = useNavigate(); // Initialize navigate function

  // Function to toggle between Sign In & Sign Up modes
  const toggleMode = () => {
    setIsSignUp(!isSignUp);
    setEmail("");
    setPassword("");
    setName("");
    setShowPassword(false);
  };

  // Effect hook to hide password when the field is empty
  useEffect(() => {
    if (password.length === 0) {
      setShowPassword(false);
    }
  }, [password]);

  // Handles form submission for Sign In/Sign Up
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!email.trim()) {
      alert("Email is required!");
      return;
    }

    setPopupMessage(isSignUp ? "âœ… Account Created Successfully!" : "ðŸš€ Welcome back! Letâ€™s continue.");
    setShowPopup(true);

    console.log(isSignUp ? "Signing Up..." : "Signing In...");
    console.log({ name, email, password });

    setTimeout(() => {
      setShowPopup(false);
      navigate("/tasks"); // Redirect after authentication
    }, 1000);
  };

  // Function to continue as a guest
  const handleGuestAccess = () => {
    console.log("Continuing as Guest...");
    navigate("/tasks"); // Redirect without authentication
  };

  return (
    <div className="auth-container">
      {/* Left Side Content */}
      <div className="auth-left">
        <h2 className="auth-title">Zidio Task <br /> Management</h2>
        <div className="auth-tagline">Manage all your tasks in one place</div>
      </div>

      {/* Right Side Authentication Box */}
      <div className="auth-box">
        <h2>{isSignUp ? "Sign Up" : "Sign In"}</h2>

        {/* Authentication Form */}
        <form onSubmit={handleSubmit}>
          {isSignUp && (
            <div className="input-group">
              <FaUser className="icon" />
              <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
          )}

          <div className="input-group">
            <FaEnvelope className="icon" />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <FaLock className="icon" />
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              minLength="6"
            />
            <span className="toggle-password" onClick={() => setShowPassword(!showPassword)}>
              {showPassword ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>

          {!isSignUp && <p className="forgot-password">Lost password? <span>Click Here!</span></p>}

          {/* Sign In/Sign Up Button */}
          <button type="submit" className="submit-btn">
            {isSignUp ? "Sign Up" : "Sign In"}
          </button>
        </form>

        {/* Continue as Guest Option */}
        <button className="guest-btn" onClick={handleGuestAccess}>
          Continue as Guest
        </button>

        {/* Switch Between Sign In and Sign Up */}
        <p className="switch-mode" onClick={toggleMode}>
          {isSignUp ? "Already have an account? Sign In" : "New user? Sign Up"}
        </p>
      </div>

      {/* Popup Notification for Feedback Messages */}
      {showPopup && (
        <div className="popup">
          <p>{popupMessage}</p>
        </div>
      )}
    </div>
  );
};

export default AuthForm;
