import React, { useState } from "react";
import "./TaskManagement.css"; // Import styles
import { FaUserCircle, FaSignOutAlt } from "react-icons/fa";
import { useNavigate } from "react-router-dom"; // Import navigation hook

const TaskManagement = () => {
  const navigate = useNavigate(); // Initialize navigate function

  // State to store task list
  const [tasks, setTasks] = useState([]);
  const [taskTitle, setTaskTitle] = useState("");
  const [description, setDescription] = useState("");
  const [assignTo, setAssignTo] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  // Function to handle logout
  const handleLogout = () => {
    localStorage.removeItem("user"); // Clear user data
    navigate("/"); // Redirect to login page
  };

  // Function to handle task submission
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!taskTitle.trim() || !description.trim() || !assignTo.trim()) {
      alert("Please fill in all required fields!");
      return;
    }

    // Create new task object
    const newTask = {
      id: Date.now(), // Unique ID
      taskTitle,
      description,
      assignTo,
      startDate,
      endDate,
    };

    // Update task list
    setTasks([...tasks, newTask]);

    // Clear input fields after submission
    setTaskTitle("");
    setDescription("");
    setAssignTo("");
    setStartDate("");
    setEndDate("");
  };

  return (
    <div className="page-container">
      {/* Separate Account Panel */}
      <div className="account-panel">
        <div className="account-info">
          <FaUserCircle className="profile-icon" />
          <div>
            <h3>Guest</h3>
            <p>guest@example.com</p>
          </div>
        </div>
        <div className="account-actions">
          <button className="logout-btn" onClick={handleLogout}>
            <FaSignOutAlt /> Logout
          </button>
        </div>
      </div>

      {/* Task Management Section */}
      <div className="task-container">
        <h2>Create Task</h2>
        <div className="task-box">
          <h3>Add Task</h3>
          <input
            type="text"
            placeholder="Task Title"
            value={taskTitle}
            onChange={(e) => setTaskTitle(e.target.value)}
          />
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            type="text"
            placeholder="Assign To"
            value={assignTo}
            onChange={(e) => setAssignTo(e.target.value)}
          />
          <div className="date-section">
            <input
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
            <input
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </div>
          <div className="btn-group">
            <button className="cancel-btn" onClick={() => {
              setTaskTitle("");
              setDescription("");
              setAssignTo("");
              setStartDate("");
              setEndDate("");
            }}>
              Cancel
            </button>
            <button className="submit-btn" onClick={handleSubmit}>
              Submit
            </button>
          </div>
        </div>

        {/* Display Task List */}
        <h2>Task List</h2>
        <div className="task-list">
          {tasks.length === 0 ? (
            <p>No tasks available.</p>
          ) : (
            tasks.map((task) => (
              <div key={task.id} className="task-item">
                <h3>{task.taskTitle}</h3>
                <p>{task.description}</p>
                <p><strong>Assigned To:</strong> {task.assignTo}</p>
                <p>
                  <strong>Start Date:</strong> {task.startDate || "N/A"} | 
                  <strong> End Date:</strong> {task.endDate || "N/A"}
                </p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default TaskManagement;
