import React, { useState, useEffect } from "react";
import "./TaskManagement.css";
import { FaUserCircle, FaCog, FaSignOutAlt } from "react-icons/fa";
import { useNavigate } from "react-router-dom"; // Import useNavigate

const TaskManagement = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate(); // Initialize navigate function

  // Fetch user from localStorage on component mount
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser)); // Convert JSON string back to object
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user"); // Clear user data on logout
    navigate("/"); // Redirect to login/signup page
  };

  return (
    <div className="page-container">
      {/* Account Panel */}
      <div className="account-panel">
        <div className="account-info">
          <FaUserCircle className="profile-icon" />
          <div>
            <h3>{user ? user.username : "Guest"}</h3>
            <p>{user ? user.email : "Not Logged In"}</p>
          </div>
        </div>
        <div className="account-actions">
          <button className="settings-btn">
            <FaCog /> Settings
          </button>
          <button className="logout-btn" onClick={handleLogout}>
            <FaSignOutAlt /> Logout
          </button>
        </div>
      </div>

      {/* Task Management Section */}
      <div className="task-container">
        <h2>Create Task</h2>
        <div className="task-box">
          <h3>Add Task</h3>
          <input type="text" placeholder="Task Title" />
          <input type="text" placeholder="Description" />
          <input type="text" placeholder="Assign To" />
          <div className="date-section">
            <input type="date" placeholder="Start Date" />
            <input type="date" placeholder="End Date" />
          </div>
          <div className="btn-group">
            <button className="cancel-btn">Cancel</button>
            <button className="submit-btn">Submit</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskManagement;
